function control($scope, $http) {

	// Get list of files
    $scope.getFiles = function () { 
        $http.get("/fileserver/getfilelist").success(
        	function (data) 
        	{
            	$scope.files = data.children;
        	}).error(
        	function (data, status) 
        	{  
        		$scope.alerts.push({ type: "error", msg: "Error downloading filelist" });
      		})
	};

	$scope.FileLoad=function(){
        var formData = new FormData();
        for(var i=0;i<document.getElementById("myFile").files.length;i++){
            formData.append("fotofile"+i, document.getElementById("myFile").files[i]);
        }
        var reader = new FileReader;
        reader.readAsDataURL(document.getElementById("myFile").files[0]);
        var place = document.getElementById("Logo");

        var xhr = new XMLHttpRequest();

        // Upload data to server
        xhr.open("POST", "/fileserver/upload", true);
        xhr.upload.onprogress = function(e) { // <<<
            if (e.lengthComputable) {
                progressBar.value = (e.loaded / e.total) * 100;
            }
        };
        xhr.upload.onloadend=function(e){
             progressBar.value = 100;

        }
        xhr.onreadystatechange=function(e){
            if (xhr.readyState == 4) {
                $scope.ddd=xhr.responseText;
                var val = document.getElementById("adjrgsaderg");
                val.value=xhr.responseText;
            }
        }
        xhr.send(formData);

        $scope.getFiles();
    };


 };

Method.Implementation:
	set st=$$$OK
	if ##class(%File).DirectoryExists(dir)
	{
		// Build resultset of all files and subfolders in a directory
		set rs=##Class(%ResultSet).%New("%File.FileSet")
 		do rs.Execute(dir,"*",,1) // build result set of all files in dir
 		while rs.Next() {
			if rs.Get("Type")'="D" //check for directories
			{
				set file=..%New()
				set file.Name=rs.Get("ItemName")
				set file.Directory=##class(%File).GetDirectory(rs.Get("Name"))
				set file.Extension=$p(file.Name,".",*)
				set file.Size=rs.Get("Size")
				set file.DateCreated=rs.Get("DateCreated")				
				set file.DateModified=rs.Get("DateModified")
				do file.%Save()
			} else {
				if (rec=1) {
					do ..AddDir(rs.Get("Name"),1)
				}
			} 
		}
	} else {
		set st = $$$ERROR($$$DirectoryNotExist,dir)
	}
	quit st

	set st=$$$OK
	if ##class(%File).Exists(filename)
	{
		try {
			set file=..%New()
			set file.Name=##class(%File).GetFilename(filename)
			set file.Directory=##class(%File).GetDirectory(filename)
			set file.Extension=$p(file.Name,".",*)
			set file.Size=##class(%File).GetFileSize(filename)
			set file.DateCreated=$ZDATETIME(##class(%File).GetFileDateCreated(filename),3)	
			set file.DateModified=$ZDATETIME(##class(%File).GetFileDateModified(filename),3)
			set st = file.%Save()
#;			w st
		} catch ex {		
			set st=ex.AsStatus()
		}
	} else {
		set st = $$$ERROR($$$FileDoesNotExist, filename)	
	}
	return file.%Id()

